import { Link } from "react-router-dom";
import db from "../Database";
import "./index.css";
function Dashboard() {
  const courses = db.courses;

  return (
    <div class="d-flex flex-column wd-flex-grow p-4">
      <h1>Dashboard</h1>
      <hr />
      <h2>Published Courses ({courses.length})</h2>
      <h3 class="d-block d-sm-none">Extra Small</h3>
      <h3 class="d-none d-sm-block d-md-none">Small</h3>
      <h3 class="d-none d-md-block d-lg-none">Medium</h3>
      <h3 class="d-none d-lg-block d-xl-none">Large</h3>
      <h3 class="d-none d-xl-block d-xxl-none">Extra Large</h3>
      <h3 class="d-none d-xxl-block">Extra Extra Large</h3>
      <div className="d-flex flex-row flex-wrap">
        {courses.map((course) => (
          <div class="dashboard-card-stretch">
          <Link key={course._id} to={`/Kanbas/Courses/${course._id}`} className="list-group-item">
            <div class="card dashboard-card-top">
              <div class="card-body">
              <h5 class="card-title text-truncate">{course.name}</h5>
              <h6 class="card-subtitle text-muted mb-2 text-truncate">{course.number}</h6>
              <h6 class="card-subtitle text-muted dashboard-card-text text-truncate dashboard-card-date">From {course.startDate} to {course.endDate}.</h6>
              </div>
            </div>
          </Link>
          </div>
        ))}
      </div>
    </div>
  );
}
export default Dashboard;
