import db from "../../Kanbas/Database";
import { Navigate, Route, Routes, useParams, useLocation }
  from "react-router-dom";
import CourseNavigation from "./CourseNavigation";
import Modules from "./Modules";
import Home from "./Home";
import Assignments from "./Assignments";
import AssignmentEditor from "./Assignments/AssignmentEditor";
import Grades from "./Grades";

function Courses() {
  const { courseId } = useParams();
  const { pathname } = useLocation();
  const course = db.courses.find(
    (course) => course._id === courseId);

  const pathElements = pathname.split('/');
  const courseIndex = pathElements.findIndex((element) => element === courseId);
  const currentScreen = pathElements[courseIndex + 1];
  const assignmentScreen = pathElements[courseIndex + 2]

  return (
    <div className="d-flex flex-column wd-flex-grow p-4">
      <nav style="--bs-breadcrumb-divider: '>';" aria-label="breadcrumb">
        <ol class="breadcrumb">
          <li class="breadcrumb-item">
            <Link
              key={index}
              to={`/Kanbas/Courses/${courseId}/Home`}
              className="">
              {course.number} {course.name}
            </Link>
          </li>{currentScreen === undefined ? "" : 
          <li class={`breadcrumb-item ${assignmentScreen === undefined && "active"}`}>
            <Link
              key={index}
              to={`/Kanbas/Courses/${courseId}/${currentScreen}`}
              className="">
              {currentScreen}
            </Link>
          </li>}{assignmentScreen === undefined ? "" :
            <li class="breadcrumb-item active">
            <Link
              key={index}
              to={`/Kanbas/Courses/${courseId}/${currentScreen}/${assignmentScreen}`}
              className="">
              {assignmentScreen}
            </Link>
          </li>
          }
        </ol>
      </nav>
      <hr />
      <div className="d-flex flex-row">
        <CourseNavigation />
        <div>
            <Routes>
                <Route path="/*" element={<Navigate to="Home" />} />
                <Route path="Home" element={<Home />} />
                <Route path="Modules" element={<Modules/>} />
                <Route path="Assignments" element={<Assignments />} />
                <Route path="Assignments/:assignmentId" element={<AssignmentEditor />} />
                <Route path="Grades" element={<Grades />} />
            </Routes>
        </div>
      </div>
    </div>
  );
}
export default Courses;
