import { Link, useParams, useLocation } from "react-router-dom";
import "./index.css";

function CourseNavigation() {
  const links = ["Home", "Modules", "Assignments", "Grades"];
  const { courseId } = useParams();
  const { pathname } = useLocation();
  return (
    <div className="d-flex flex-column course-nav-sidebar">
      {links.map((link, index) => (
        <Link
          key={index}
          to={`/Kanbas/Courses/${courseId}/${link}`}
          className={`list-group-item link-underline link-underline-opacity-0 link-underline-opacity-75-hover course-nav-link ${pathname.includes(link) && "course-nav-link-active"}`}>
          {link}
        </Link>
      ))}
    </div>
  );
}


export default CourseNavigation;
