import { Link, useLocation } from "react-router-dom";
import "./index.css";
function KanbasNavigation() {
  const links = ["Account", "Dashboard", "Courses", "Calendar"];
  const { pathname } = useLocation();
  return (
    <div className="list-group list-group-flush kanbas-navigation-bar text-center d-flex flex-column">
      {links.map((link, index) => (
        <Link
          key={index}
          to={`/Kanbas/${link}`}
          className={`list-group-item kanbas-navigation-bar-block kanbas-navigation-bar-anchor ${pathname.includes(link) && "active"}`}>
          {link}
        </Link>
      ))}
    </div>
  );
}
export default KanbasNavigation;
